CREATE TABLE `categorys` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `user_id` int(10) unsigned NOT NULL COMMENT 'user that created the category',  `parent_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'category id to which it is a sub category of. parent categories have their parent_id as 0',  `title` varchar(255) DEFAULT NULL,  `description` text,  `access_type` tinyint(4) NOT NULL DEFAULT '0' COMMENT '-1 for private, 0 for public, 1 for shared',  `date_created` char(20) DEFAULT NULL,  `active` tinyint(3) unsigned NOT NULL DEFAULT '1',  `deleted` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT 'this indicates if a category has been permanently deleted from the recycle bin',  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=latin1;CREATE TABLE `content_categorys` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `category_id` int(10) unsigned NOT NULL,  `content_id` int(10) unsigned NOT NULL,  `user_id` int(10) unsigned NOT NULL COMMENT 'user that assigned the content to the category',  `date_created` char(20) DEFAULT NULL,  `active` tinyint(4) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=latin1;CREATE TABLE `group_categorys` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `category_id` int(10) unsigned NOT NULL,  `group_id` int(10) unsigned NOT NULL,  `user_id` int(10) unsigned NOT NULL COMMENT 'user that assigned the category to the group',  `date_created` char(20) DEFAULT NULL,  `active` tinyint(4) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=latin1;alter table `mail_queue` change `body` `body` text  NOT NULL  COLLATE latin1_swedish_ci , change `from` `from` varchar(255)  NOT NULL  COLLATE latin1_swedish_ci , change `to` `to` text  NOT NULL  COLLATE latin1_swedish_ci , change `cc` `cc` text  NULL  COLLATE latin1_swedish_ci , change `bcc` `bcc` text  NULL  COLLATE latin1_swedish_ci , change `attachment` `attachment` text  NULL  COLLATE latin1_swedish_ci 